import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"

export default function ArticlePage() {
    const [articleInfo, setArticleInfo] = useState(null);
    const { id } = useParams();
    const domParser = new DOMParser();
    useEffect(() => {
        fetch(`http://localhost:4000/post/${id}`)
            .then(response => {
                response.json().then(articleInfo => {
                    setArticleInfo(articleInfo);
                })
            })
    }, []);

    if (!articleInfo) {
        return '';
    } else {
        const body = articleInfo.article.body;
        const bodyModified = domParser.parseFromString(body, "text/html").body.getElementsByTagName("*");
        console.log(bodyModified);
        return (
            <div className="article">
                <div className="left">
                    <div className="tableOfContents">
                        <h2>Table of Contents</h2>
                        <ul>
                            <li>Pros & Cons</li>
                            <li>Second Item</li>
                            <li>Third Item</li>
                        </ul>
                    </div>
                    <div className="ad">
                        AD
                    </div>
                </div>
                <div className="centre">
                    <div className="info">
                        <h3 className="tag">{articleInfo.article.tags.join(' > ')}</h3>
                        <h1 className="title">{articleInfo.article.title}</h1>
                        <div className="subInfo">
                            <div className="people">
                                <div className="authors">
                                    <h3>Authors:</h3>
                                    {articleInfo.authors.length > 0 && articleInfo.authors.map(author => {
                                        return <p className="author">{author}</p>
                                    })}
                                </div>
                                <div className="editors">
                                    <h3>Editors:</h3>
                                    {articleInfo.editors.length > 0 && articleInfo.editors.map(editor => {
                                        return <p className="editor">{editor}</p>
                                    })}
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="image">
                        <img src="https://www.investopedia.com/thmb/-pHVGUrLA2OzieX3XXDcz8fSAn0=/750x0/filters:no_upscale():max_bytes(150000):strip_icc():format(webp)/GettyImages-1246026356-22220ea971dc4eff8d5fe628cb9cba98.jpg"></img>
                    </div>
                    {/* <div className="ad">
                        AD
                    </div> */}
                    <div className="content">
                        {bodyModified}
                    </div>
                    <div>
                        <h2>Table of Contents</h2>
                    </div>
                    <div>
                        <h2>Referencias</h2>
                        {articleInfo.article.refrences.length > 0 && articleInfo.article.refrences.map(refrence => {
                            return <p>{refrence.name}</p>
                        })}
                    </div>
                </div>
                <div className="right">
                    <div className="ad">
                        AD
                    </div>
                </div>
            </div>
        )
    }
}
