import { Link } from "react-router-dom";

export default function ArticleIcon({ article, _id, authors, editors }) {
    const tags = article.tags.join(' > ');
    console.log(article)
    console.log("passed")
    return (
        < div className="post">
            <div className="image">
                <Link to={'/post/_id'} >
                    {/* <img src={article.photos}></img> */}
                </Link>
            </div>
            <div className="text">
                <Link to={'/post/_id'} >
                    <h2 style={{ textDecoration: 'none' }}>{article.title}</h2>
                </Link>

                <div className="info">
                    <div className="people">
                        <div className="authors">
                            <h3>Authors:</h3>
                            {authors.length > 0 && authors.map(author => {
                                return <p className="author">{author}</p>
                            })}
                        </div>
                        <div className="editors">
                            <h3>Editors:</h3>
                            {editors.length > 0 && editors.map(editor => {
                                return <p className="editor">{editor}</p>
                            })}
                        </div>
                    </div>
                    <time>April 23rd 2024</time>
                </div>
                <h3 className="tag">{tags}</h3>
                <p>{article.summary}</p>
            </div>
        </div>
    );
}
